INSERT INTO `tb_blog` VALUES ('29', '<h3>今天给大家介绍一下如何利用JPA实现表关联查询。</h3><p>今天给大家举一个一对多的关联查询，并且是使用JPA原生的findBy语句实现的。</p><p>例子中总共有两个实体类，一个是Floor（商品楼层类），另一个是FloorContent（商品楼层内容表）。下面看两张表的源代码：</p><p>Floor类：</p><p><b>[java]</b>&nbsp;<a href=\"https://blog.csdn.net/linzhiqiang0316/article/details/53022683#\" target=\"_self\">view plain</a>&nbsp;<a href=\"https://blog.csdn.net/linzhiqiang0316/article/details/53022683#\" target=\"_self\">copy</a></p><ol><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 102, 153); font-size: 14px;\">package</span>&nbsp;cms.model;&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 102, 153); font-size: 14px;\">import</span>&nbsp;cms.model.base.BaseDomain;&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 102, 153); font-size: 14px;\">import</span>&nbsp;org.hibernate.annotations.GenericGenerator;&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\">&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 102, 153); font-size: 14px;\">import</span>&nbsp;javax.persistence.*;&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 102, 153); font-size: 14px;\">import</span>&nbsp;java.io.Serializable;&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 102, 153); font-size: 14px;\">import</span>&nbsp;java.util.List;&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 130, 0);\">/**</span>&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 130, 0);\">&nbsp;*&nbsp;Created&nbsp;by&nbsp;Roney&nbsp;on&nbsp;2016/10/10.</span>&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 130, 0);\">&nbsp;*&nbsp;楼层管理</span>&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 130, 0);\">&nbsp;*</span>&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 130, 0);\">&nbsp;*/</span>&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(100, 100, 100);\">@Entity</span>&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(100, 100, 100);\">@Table</span>(indexes&nbsp;=&nbsp;{<span style=\"color: rgb(100, 100, 100);\">@Index</span>(name&nbsp;=&nbsp;<span style=\"color: rgb(0, 0, 255);\">\"idx_floor_user\"</span>,columnList&nbsp;=&nbsp;<span style=\"color: rgb(0, 0, 255);\">\"user_id\"</span>)})&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 102, 153); font-size: 14px;\">public</span>&nbsp;<span style=\"color: rgb(0, 102, 153); font-size: 14px;\">class</span>&nbsp;Floor&nbsp;<span style=\"color: rgb(0, 102, 153); font-size: 14px;\">extends</span>&nbsp;BaseDomain&nbsp;<span style=\"color: rgb(0, 102, 153); font-size: 14px;\">implements</span>&nbsp;Serializable&nbsp;{&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\">&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(100, 100, 100);\">@Id</span>&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(100, 100, 100);\">@GenericGenerator</span>(name&nbsp;=&nbsp;<span style=\"color: rgb(0, 0, 255);\">\"PKUUID\"</span>,&nbsp;strategy&nbsp;=&nbsp;<span style=\"color: rgb(0, 0, 255);\">\"uuid2\"</span>)&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(100, 100, 100);\">@GeneratedValue</span>(generator&nbsp;=&nbsp;<span style=\"color: rgb(0, 0, 255);\">\"PKUUID\"</span>)&nbsp;&nbsp;</span></li><li><span style=\"color: rgb(0, 0, 0);\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(100, 100, 100);\">@Column</span>(length&nbsp;=&nbsp;<span style=\"color: rgb(192, 0, 0);\">36</span>)&nbsp;&nbsp;</